using namespace std;


int n,K;
namespace _1{// 1+1+2+3
	int mn,bal;
	void main(){
		for(int i=2;i<=n;++i){
			if(n%i==0){
				mn=i;break;
			}
		}
		mn=n/mn;
		bal=K/(n/mn);
		int cnt=0;
		for(int i=0;i<n;++i){
			if(i%mn<bal)
				putchar('1'),++cnt;
			else
				putchar('0');
		}
	}
}
vector<int> V;

namespace _2_fake{
#define ull unsigned long long
	int now[40];
	int maxn[40],maxans;
	unordered_set<ull> vis;
	ull has(){
		ull ret=1;
		for(int i=0;i<n;++i)
			ret=113*ret+513*now[i];
		return ret;
	}
	void dfs(int nowans){
		ull g=has();
		if(vis.find(g)!=vis.end())return;
		vis.insert(g);
		if(nowans>maxans){
			maxans=nowans;
			memcpy(maxn,now,sizeof now);
		}
		for(int i:V){
			if(nowans+i>K)continue;
			for(int j=0;j<n/i;++j){
				int fl=0;
				for(int k=j;k<n;k+=n/i)
					fl|=now[k];
				if(fl==1)continue;
				for(int k=j;k<n;k+=n/i)
					now[k]=1;
				dfs(nowans+i);
				for(int k=j;k<n;k+=n/i)
					now[k]=0;
			}
		}
	}
	void main(){
		dfs(0);
		for(int i=0;i<n;++i)
			printf("%d",maxn[i]);
	}
#undef ull
}

