namespace _3{// 3+16
	int col[1000005];
	int del[1000005],mnid=-1,mn=1e9;
	set<int> S;
	void main(){
		for(int i=0;i<n;++i)
			col[i]=i%(n/V[0]);
		for(int i=0;i<n/V[0];++i)
			S.insert(i);
		for(int i=0,now=0,j=0;i<=(n/V[1]);++i){
			if(i*V[1]>K)
				break;
			if(i!=0){
				for(int o=now;o<n;o+=n/V[1]){
					if(S.find(col[o])!=S.end())
						S.erase(col[o]);
				}
				now+=(n/V[0])/V[1];
				if(now>=(n/V[1]))
					j++,now=j;
			}
			int rem=K-i*V[1];
			if(V[0]*(int)S.size()>=rem)
				rem=rem%V[0];
			else
				rem=rem-V[0]*S.size();
			if(mn>rem){
				mn=rem,mnid=i;
			}
		}
		S.clear();
		for(int i=0;i<n/V[0];++i)
			S.insert(i);
		for(int i=0,now=0,j=0;i<=mnid;++i){
			if(i!=0){
				for(int o=now;o<n;o+=n/V[1]){
					if(S.find(col[o])!=S.end())
						S.erase(col[o]);
					del[o]=1;
				}
				now+=(n/V[0])/V[1];
				if(now>=(n/V[1]))
					j++,now=j;
			}
		}
		int rem=K-mnid*V[1];
		while(rem>=V[0] && S.size()){
			int C=*S.begin();
			S.erase(S.begin());
			for(int o=C;o<n;o+=n/V[0]){
				del[o]=1;
			}
			rem-=V[0];
		}
		int cnt=0;
		for(int i=0;i<n;++i)
			printf("%d",del[i]),cnt+=del[i];
	}
}



int np[1000005];
int main(){
	freopen("ur.in","r",stdin);
	freopen("ur.out","w",stdout);
	scanf("%d %d",&n,&K);
	for(int i=2;i<=n;++i){
		if(np[i]==0){
			if(n%i==0)V.push_back(i);
			for(int j=i*2;j<=n;j+=i)
				np[j]=1;
		}
	}
	if(n<=36)
		_2::main();
	else if(n==K || K<=1 || V.size()==1)
		_1::main();
	else if(V.size()>=2)
		_3::main();
}
